using UnityEngine;
using System.Collections;

public class GenLayerRiverInit : GenLayer {

	public GenLayerRiverInit(long seed, GenLayer parentGenLayer) : base(seed)
	{
		parent = parentGenLayer;
	}

	/**
     * Returns a list of integer values generated by this layer. These may be interpreted as temperatures, rainfall
     * amounts, or biomeList[] indices based on the particular GenLayer subclass.
     */
	public override int[] getInts(int areaX, int areaY, int areaWidth, int areaHeight)
	{
		int[] aint = parent.getInts(areaX, areaY, areaWidth, areaHeight);
		int[] aint1 = IntCache.getIntCache(areaWidth * areaHeight);

		for (int i = 0; i < areaHeight; ++i)
		{
			for (int j = 0; j < areaWidth; ++j)
			{
				initChunkSeed((long)(j + areaX), (long)(i + areaY));
				aint1[j + i * areaWidth] = aint[j + i * areaWidth] > 0 ? nextInt(299999) + 2 : 0;
			}
		}

		return aint1;
	}
}
